export const getDummySessionDetail = (sessionId: string) => {
  const session = dummySession.find((item) => item.id === sessionId);

  if (!session) {
    return null;
  }

  return {
    session,
    transcribe:
      dummyTranscribe.find((item) => item.session_id === sessionId) || null,
    progressNotes: dummyProgressNote.filter(
      (note) => note.session_id === sessionId
    ),
  };
};

export const isDummySessionId = (sessionId: string) =>
  dummySession.some((session) => session.id === sessionId);
